import type { CollectionOptions } from '@zag-js/combobox';
import * as combobox from '@zag-js/combobox';
import { type PropTypes } from '@zag-js/react';
import { type CollectionItem, type Optional } from '../types';
export interface UseComboboxProps<T extends CollectionItem> extends CollectionOptions<T>, Omit<Optional<combobox.Context<T>, 'id'>, 'collection'> {
    /**
     * the initial value of the combobox
     */
    defaultValue?: combobox.Context<T>['value'];
}
export interface UseComboboxReturn<T extends CollectionItem> extends combobox.Api<PropTypes, T> {
}
export declare const useCombobox: <T extends CollectionItem>(props: UseComboboxProps<T>) => UseComboboxReturn<T>;
