'use client';
'use strict';

Object.defineProperty(exports, Symbol.toStringTag, { value: 'Module' });

const react = require('react');

const useIsServer = () => {
  const [isServer, setServer] = react.useState(true);
  react.useEffect(() => {
    setServer(false);
  }, []);
  return isServer;
};

exports.useIsServer = useIsServer;
